mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select id, category_id, name, price, status, description, image, create_time, update_time, create_user, update_user from setmeal
and name like concat('%',#{name},'%')
and category_id = #{categoryId}
and status = #{status}
